import { CartItem } from "../types";
import { Item } from "./Item";

export const Items = (items: CartItem[], toggleItem: (id: number) => void) => (
  <>
    <ul>
      {items.map((item) => (
        <Item key={item.id} item={item} toggleItem={toggleItem} />
      ))}
    </ul>
    <button>
      {items.some((item) => item.selected) ? "Pay separately" : "Pay"}
    </button>
    {items.some((item) => item.payed) && <button>Pay rest of the bill</button>}
  </>
);
