import { useState, useEffect } from "react";
import classNames from "classnames";
import css from "./styles.module.scss";

interface Item {
  id: number;
  name: string;
  price: string;
  payed: boolean;
  selected: boolean;
}

type ItemProps = {
  item: Item;
  handleChange: (element: any) => void;
};

const Item = ({ item, handleChange }: ItemProps) => (
  <li>
    <button className={classNames({ [css.payed]: item.payed })}>
      <span>{item.name}</span>
      <span>{item.price}</span>
      <input type="checkbox" onChange={() => handleChange} />
    </button>
  </li>
);

export const Cart = () => {
  const [items, setItems] = useState<Item[]>([]);

  const handleChange = (element: any) => {
    debugger;
  };

  useEffect(() => {
    setItems([
      { id: 1, name: "Espresso", price: "5.00€", payed: false },
      { id: 2, name: "Espresso", price: "5.00€", payed: true },
      { id: 3, name: "Orange Juice", price: "7.50€", payed: false },
      { id: 4, name: "Club Sandwich", price: "12.00€", payed: false },
      { id: 5, name: "Club Soda", price: "2.75€", payed: false },
    ]);
  }, []);

  return (
    <ul>
      {items.map((item) => (
        <Item key={item.id} item={item} handleChange={handleChange} />
      ))}
    </ul>
  );
};
